// frontend/src/api.js
import axios from 'axios';

const API_URL = 'http://localhost:3001/api';

export const registerUser = async (username, password) => {
    return await axios.post(`${API_URL}/auth/register`, { username, password });
};

export const loginUser = async (username, password) => {
    return await axios.post(`${API_URL}/auth/login`, { username, password });
};

export const getProgress = async (userId) => {
    return await axios.get(`${API_URL}/progress/${userId}`);
};

export const updateProgress = async (userId, progress) => {
    return await axios.put(`${API_URL}/progress/${userId}`, { progress });
};

export const getSubscriptions = async () => {
    return await axios.get(`${API_URL}/subscription`);
};

export const createSubscription = async (userId, planId) => {
    return await axios.post(`${API_URL}/subscription`, { userId, planId });
};

export const getQuizzes = async () => {
    return await axios.get(`${API_URL}/quiz`);
};

export const createQuiz = async (title, questions) => {
    return await axios.post(`${API_URL}/quiz`, { title, questions });
};

export const getPerformance = async (userId) => {
    return await axios.get(`${API_URL}/performance/${userId}`);
};

export const updatePerformance = async (userId, marks, feedback) => {
    return await axios.put(`${API_URL}/performance/${userId}`, { marks, feedback });
};
