// src/components/Home.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import logo from './assets/D.png'; // Ensure the path to your logo is correct

const Home = () => {
  // State for the counts
  const [studentsAppearing, setStudentsAppearing] = useState(0);
  const [studentsCleared, setStudentsCleared] = useState(0);

  useEffect(() => {
    const appearingInterval = setInterval(() => {
      setStudentsAppearing((prev) => {
        if (prev < 1415100) {
          return prev + 1000; 
        }
        clearInterval(appearingInterval);
        return prev;
      });
    }, 10);

    const clearedInterval = setInterval(() => {
      setStudentsCleared((prev) => {
        if (prev < 49164) {
          return prev + 100; 
        }
        clearInterval(clearedInterval);
        return prev;
      });
    }, 20);

    return () => {
      clearInterval(appearingInterval);
      clearInterval(clearedInterval);
    };
  }, []);

  return (
    <div style={{ textAlign: 'center', padding: '20px', backgroundColor: '#F7F7F7', minHeight: '100vh' }}>
      {/* Logo at the top left corner */}
      <img src={logo} alt="Website Logo" style={{ width: '100px', position: 'absolute', top: '20px', left: '20px' }} />

      <h1 style={{ 
          fontSize: '48px', 
          margin: '20px 0', 
          fontWeight: 'bold', 
          background: 'linear-gradient(to right, #4A90E2, #F39C12)', 
          WebkitBackgroundClip: 'text', 
          color: 'transparent',
          textShadow: '1px 1px 2px rgba(0, 0, 0, 0.1)',
        }}>
        Welcome to Web DailiGo
      </h1>
      <p style={{ fontSize: '24px', margin: '20px 0', color: '#333' }}>
        Your one-stop platform for managing your learning experience. 
        <br />
        Whether you're a new student or a returning learner, we have something for you!
      </p>

      {/* Statistics Section */}
      <div style={{ marginTop: '50px' }}>
        <h2 style={{ fontSize: '36px', color: '#9B1B30' }}>Statistics</h2>
        <div style={{ display: 'flex', justifyContent: 'center', margin: '20px 0' }}>
          <div style={{ 
              backgroundColor: '#4A90E2', 
              color: 'white', 
              padding: '20px', 
              borderRadius: '5px', 
              margin: '0 20px', 
              fontSize: '24px', 
              width: '250px',
              boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)'
            }}>
            <h3>Students Appearing for JEE:</h3>
            <p style={{ fontSize: '48px', fontWeight: 'bold' }}>{studentsAppearing}</p>
          </div>
          <div style={{ 
              backgroundColor: '#F39C12', 
              color: 'white', 
              padding: '20px', 
              borderRadius: '5px', 
              margin: '0 20px', 
              fontSize: '24px', 
              width: '250px',
              boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)'
            }}>
            <h3>Students Cleared Exam:</h3>
            <p style={{ fontSize: '48px', fontWeight: 'bold' }}>{studentsCleared}</p>
          </div>
        </div>
      </div>

      <p style={{ fontSize: '20px', margin: '20px 0', color: '#333' }}>
        New here? Register now and start your journey!
      </p>
      <p style={{ 
          fontSize: '18px', 
          margin: '20px 0', 
          maxWidth: '800px', 
          marginLeft: 'auto', 
          marginRight: 'auto',
          color: '#555' 
        }}>
        We’re dedicated to guiding JEE aspirants toward their goals with a structured, organized approach. 
        Let us turn your confusion into a clear, step-by-step plan for exam success. Join us today and unlock your true potential!
      </p>

      <div style={{ marginTop: '30px' }}>
        <Link to="/register" style={{ 
          textDecoration: 'none', 
          marginRight: '20px', 
          backgroundColor: '#4A90E2', 
          color: 'white', 
          padding: '15px 30px', 
          fontSize: '20px', 
          borderRadius: '5px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
          transition: 'background-color 0.3s'
        }}>
          Register
        </Link>
      </div>

      {/* Help Callback Section */}
      <div style={{
          marginTop: '50px', 
          backgroundColor: '#E74C3C', 
          color: 'white', 
          padding: '30px',
          borderRadius: '10px',
          maxWidth: '800px',
          margin: '20px auto',
          boxShadow: '0 4px 20px rgba(0, 0, 0, 0.1)',
        }}>
        <h2 style={{ 
            fontSize: '28px', 
            marginBottom: '15px', 
            fontWeight: 'bold', 
            textAlign: 'center' 
          }}>
          Need Help? Get a Callback!
        </h2>
        <p style={{ 
            fontSize: '18px', 
            lineHeight: '1.6', 
            textAlign: 'center', 
            marginBottom: '20px' 
          }}>
          Have any questions or need guidance on how to use Dailigo? Our team is here to help! 
          Whether it's about navigating the platform, finding the right mentor, or resolving technical issues, 
          simply request a callback and we’ll get in touch.
        </p>

        <h3 style={{ 
            fontSize: '24px', 
            marginBottom: '10px', 
            fontWeight: 'bold', 
            textAlign: 'center' 
          }}>
          Contact Details
        </h3>
        
        {/* Contact Details Container */}
        <div style={{
            display: 'flex', 
            justifyContent: 'center', 
            flexDirection: 'column', 
            alignItems: 'center', 
            marginBottom: '20px',
          }}>
          <p style={{ fontSize: '18px', margin: '5px 0' }}>
            <strong>Name:</strong> Gaurav Rastogi
          </p>
          <p style={{ fontSize: '18px', margin: '5px 0' }}>
            <strong>Phone:</strong> +91-9628233872
          </p>
          <p style={{ fontSize: '18px', margin: '5px 0' }}>
            <strong>Email:</strong> <a href="mailto:dailigo@gmail.com" style={{ color: 'lightblue', textDecoration: 'underline' }}>dailigo@gmail.com</a>
          </p>
        </div>

        {/* Callback Button */}
        <div style={{ textAlign: 'center' }}>
          <button style={{
              padding: '10px 20px', 
              fontSize: '16px', 
              marginTop: '10px', 
              backgroundColor: '#4A90E2', 
              color: 'white', 
              border: 'none', 
              borderRadius: '5px',
              cursor: 'pointer',
              transition: 'background-color 0.3s'
            }} 
            onMouseEnter={e => e.target.style.backgroundColor = '#007BFF'} 
            onMouseLeave={e => e.target.style.backgroundColor = '#4A90E2'}>
            Place a Callback
          </button>
        </div>
      </div>

      {/* Login Button positioned at the corner */}
      <Link to="/login" style={{
          position: 'fixed',
          top: '20px',
          right: '20px',
          textDecoration: 'none',
          backgroundColor: '#4A90E2',
          color: 'white',
          padding: '10px 20px',
          borderRadius: '5px',
          boxShadow: '0 4px 8px rgba(0, 0, 0, 0.1)',
        }}>
        Login
      </Link>

      {/* Footer Section */}
      <footer style={{
          backgroundColor: '#333',
          color: 'white',
          padding: '20px 0',
          position: 'relative',
          marginTop: '40px',
          textAlign: 'center',
        }}>
        <p>&copy; {new Date().getFullYear()} Web DailiGo. All rights reserved.</p>
      </footer>
    </div>
  );
};

export default Home;
