// src/components/Home.js
import React, { useState } from 'react';
import Register from './Register';
import Login from './Login';

const Home = () => {
  const [showRegister, setShowRegister] = useState(false);
  const [showLogin, setShowLogin] = useState(false);

  const handleRegisterClick = () => {
    setShowRegister(true);
    setShowLogin(false); // Hide login form if register is clicked
  };

  const handleLoginClick = () => {
    setShowLogin(true);
    setShowRegister(false); // Hide register form if login is clicked
  };

  return (
    <div style={{ textAlign: 'center' }}>
      <h1>Welcome to Home Page</h1>
      <p>Please register or log in to access more features.</p>
      
      <div>
        <button 
          onClick={handleRegisterClick} 
          style={{ 
            backgroundColor: 'blue', 
            color: 'white', 
            padding: '15px 30px', 
            fontSize: '18px', 
            border: 'none', 
            borderRadius: '5px', 
            cursor: 'pointer', 
            margin: '10px' // Adds margin between buttons
          }}>
          Register
        </button>

        <button 
          onClick={handleLoginClick} 
          style={{ 
            backgroundColor: 'blue', 
            color: 'white', 
            padding: '15px 30px', 
            fontSize: '18px', 
            border: 'none', 
            borderRadius: '5px', 
            cursor: 'pointer', 
            margin: '10px' // Adds margin between buttons
          }}>
          Login
        </button>
      </div>

      {showRegister && (
        <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
          <Register />
        </div>
      )}

      {showLogin && (
        <div style={{ display: 'flex', justifyContent: 'center', marginTop: '20px' }}>
          <Login />
        </div>
      )}
    </div>
  );
};

export default Home;
