// frontend/src/components/Progress.js
import React, { useState, useEffect } from 'react';
import { getProgress, updateProgress } from '../api';

const Progress = ({ userId }) => {
    const [progress, setProgress] = useState(0);

    useEffect(() => {
        const fetchProgress = async () => {
            const response = await getProgress(userId);
            setProgress(response.data.progress);
        };
        fetchProgress();
    }, [userId]);

    const handleUpdate = async () => {
        await updateProgress(userId, progress);
    };

    return (
        <div>
            <h1>User Progress</h1>
            <p>Current Progress: {progress}%</p>
            <input
                type="number"
                value={progress}
                onChange={(e) => setProgress(e.target.value)}
                placeholder="Update Progress"
            />
            <button onClick={handleUpdate}>Update Progress</button>
        </div>
    );
};

export default Progress;
